mapper.update:
update user2 set
enabled=1, authority='ROLE_USER' where user_email=#{user_email, jdbcType=VARCHAR, javaType=Object} and
user_email_key=#{user_email_key, jdbcType=VARCHAR, javaType=Object}

mapper.select:
select user_id
	from user2
	where user_id = #{user_id}

select user_email
	from user2
	where user_email = #{user_email}

mapper.update:
update user2
set user_password = #{user_password}
where user_id=#{user_id}
