mapper.select:
select * from
(select A.*, ROWNUM r from
	(select * from post, user2 where post_category = ${post_category} and post.post_usernum = user2.user_num
	order by post_num desc) A
		)
	where r >= #{start} and r <= #{end}

select * from
	(select A.*, ROWNUM r from
		(select * from post order by post_num desc) A
	)
where r >= #{start} and r <= #{end}

mapper.update:
update post set
post_readcount = post_readcount+1
where post_num = #{post_num}
